SELECT DEPARTMENT.Dnumber, Dname, COUNT(Dlocation) AS Number_of_Locations FROM DEPARTMENT INNER JOIN DEPT_LOCATIONS on DEPARTMENT.Dnumber = DEPT_LOCATIONS.Dnumber WHERE mgr_ssn in (SELECT Essn FROM DEPENDENT WHERE Sex = "F" GROUP BY Essn HAVING COUNT(DEPENDENT_name) >= 2) GROUP BY DEPARTMENT.Dnumber;
