SELECT mgr_ssn, Dnumber, COUNT(Dependent_name)  AS Number_of_Dependents FROM DEPARTMENT INNER JOIN DEPENDENT ON DEPARTMENT.mgr_ssn = DEPENDENT.Essn WHERE mgr_ssn IN  (SELECT mgr_ssn FROM DEPARTMENT INNER JOIN DEPT_LOCATIONS ON DEPARTMENT.Dnumber = DEPT_LOCATIONS.Dnumber GROUP BY DEPARTMENT.Dnumber HAVING COUNT(Dlocation) > 1) GROUP BY mgr_ssn, Dnumber;
